import osimport timeimport psutilimport easygui# Browse folderbrowserPath = easygui.diropenbox()os.chdir(browserPath)files = os.listdir(browserPath)# Create 'Fisiere_Printate' if it doesn't exist.# orig_dir = os.getcwd()# if not os.path.exists('Fisiere_Printate'):#     os.makedirs('Fisiere_Printate')# os.chdir('{}\\Fisiere_Printate'.format(orig_dir))# PrintedFiles = os.getcwd()# if "Fisiere_Printate" in files:#     files.remove("Fisiere_Printate")for file in files:    if ".pdf" in file:        print("Se printeaza fisierul {}/{} '{}'".format(files.index(file) + 1, len(files), file))        os.startfile((os.path.join(browserPath, file)), "print")        time.sleep(2)        while "Acrobat.exe" in (i.name() for i in psutil.process_iter()) is True:            time.sleep(1)        # shutil.move('{}\\{}'.format(orig_dir, file), "{}".format(PrintedFiles))input()